/*---------------------------------------------------------------------------------**//**
* Parametrized conversion tests.
* @bsimethod
+---------------+---------------+---------------+---------------+---------------+------*/
TEST_P(SpecificTransformTests, SpecificCoordConversionTest)
{
    conversionTest theConversionTestParam = GetParam(); 

    GeoCoordinates::BaseGCSPtr sourceGCS = GeoCoordinates::BaseGCS::CreateGCS(theConversionTestParam.m_sourceGCS.c_str());
    GeoCoordinates::BaseGCSPtr targetGCS = GeoCoordinates::BaseGCS::CreateGCS(theConversionTestParam.m_targetGCS.c_str());

    ASSERT_TRUE(sourceGCS.IsValid() && sourceGCS->IsValid());
    ASSERT_TRUE(targetGCS.IsValid() && targetGCS->IsValid());

    sourceGCS->SetVerticalDatumByKey(theConversionTestParam.m_sourceVerticalDatum.c_str());
    targetGCS->SetVerticalDatumByKey(theConversionTestParam.m_targetVerticalDatum.c_str());

    DPoint3d resultPoint = {0,0,0};
    DPoint3d inputPoint;

    inputPoint.x = theConversionTestParam.m_inputCoordinateX;
    inputPoint.y = theConversionTestParam.m_inputCoordinateY;
    inputPoint.z = theConversionTestParam.m_inputCoordinateZ;


    EXPECT_TRUE(REPROJECT_Success == sourceGCS->CartesianFromCartesian(resultPoint, inputPoint, *targetGCS));

    EXPECT_NEAR(resultPoint.x, theConversionTestParam.m_outputCoordinateX, theConversionTestParam.m_tolerance);
    EXPECT_NEAR(resultPoint.y, theConversionTestParam.m_outputCoordinateY, theConversionTestParam.m_tolerance);
    EXPECT_NEAR(resultPoint.z, theConversionTestParam.m_outputCoordinateZ, theConversionTestParam.m_tolerance);
}

    
INSTANTIATE_TEST_SUITE_P(SpecificTransformTests_Combined,
                        SpecificTransformTests,
                        ValuesIn(s_listOfConversionTests));





   


